CREATE OR REPLACE FUNCTION obter_se_exige_inspecao_nf (cd_material_p bigint, nr_sequencia_p bigint, ie_opcao_p text default 'I') RETURNS varchar AS $body$
DECLARE
						


cd_grupo_material_w			smallint;
cd_subgrupo_material_w			smallint;
cd_classe_material_w			integer;
ie_exige_inspecao_w			varchar(1) := 'N';
ie_exige_conf_fisica_w		exige_inspecao_item_nf.ie_exige_conf_fisica%type := 'N';
cd_operacao_nf_w				exige_inspecao_item_nf.cd_operacao_nf%Type;
nr_sequencia_w				exige_inspecao_item_nf.nr_sequencia%Type;
cd_cgc_emitente_w			pessoa_juridica.cd_cgc%type;
qt_registros_w				bigint;
cd_estab_lib_w				exige_inspecao_item_nf.cd_estab_lib%type := wheb_usuario_pck.get_cd_estabelecimento;

c01 CURSOR FOR
SELECT	coalesce(ie_exige_inspecao,'S') ie_exige_inspecao,
	nr_sequencia,
	coalesce(ie_exige_conf_fisica, 'N') ie_exige_conf_fisica
from	exige_inspecao_item_nf
where	coalesce(cd_grupo_material, cd_grupo_material_w)		= cd_grupo_material_w
and	coalesce(cd_subgrupo_material, cd_subgrupo_material_w)	= cd_subgrupo_material_w
and	coalesce(cd_classe_material, cd_classe_material_w)		= cd_classe_material_w
and (coalesce(cd_material, cd_material_p) 			= cd_material_p or cd_material_p = 0)
and	coalesce(cd_operacao_nf, cd_operacao_nf_w)		= cd_operacao_nf_w
and	coalesce(cd_estab_lib, cd_estab_lib_w)			= cd_estab_lib_w
order by
	coalesce(cd_material, 0),
	coalesce(cd_classe_material, 0),
	coalesce(cd_subgrupo_material, 0),
	coalesce(cd_grupo_material, 0);
	

BEGIN

select	a.cd_grupo_material,
	a.cd_subgrupo_material,
	a.cd_classe_material
into STRICT	cd_grupo_material_w,
	cd_subgrupo_material_w,
	cd_classe_material_w
from	estrutura_material_v a
where	a.cd_material = cd_material_p;

select 	cd_operacao_nf,
	cd_cgc_emitente
into STRICT 	cd_operacao_nf_w,
	cd_cgc_emitente_w
from 	nota_fiscal
where 	nr_sequencia = nr_sequencia_p;
		
open C01;
loop
fetch C01 into	
	ie_exige_inspecao_w,
	nr_sequencia_w,
	ie_exige_conf_fisica_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin	
	ie_exige_inspecao_w	:= ie_exige_inspecao_w;
	nr_sequencia_w		:= nr_sequencia_w;
	ie_exige_conf_fisica_w := ie_exige_conf_fisica_w;
	end;
end loop;
close C01;

select	count(*)
into STRICT	qt_registros_w
from	exige_inspecao_item_nf_for
where	nr_seq_regra = nr_sequencia_w;

if (qt_registros_w > 0) then

	select	count(*)
	into STRICT	qt_registros_w
	from	exige_inspecao_item_nf_for
	where	nr_seq_regra = nr_sequencia_w
	and	cd_cnpj = cd_cgc_emitente_w;
	
	if (qt_registros_w = 0) then
		ie_exige_inspecao_w := 'N';
		ie_exige_conf_fisica_w := 'N';
	end if;
end if;

if (ie_opcao_p = 'I') then
	return coalesce(ie_exige_inspecao_w,'N');
else
	return coalesce(ie_exige_conf_fisica_w,'N');
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
