CREATE OR REPLACE FUNCTION obter_se_exige_justif_div_nf ( nr_seq_regra_p bigint, cd_perfil_p bigint, cd_setor_atendimento_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ie_permite_justif_w	varchar(1) := 'S';
cd_tipo_divergencia_w	integer;
cd_estabelecimento_w	bigint;
nr_sequencia_w		bigint;
qt_registros_w		bigint;


BEGIN

select	coalesce(ie_permite_justif,'S'),
	cd_tipo_divergencia,
	cd_estabelecimento
into STRICT	ie_permite_justif_w,
	cd_tipo_divergencia_w,
	cd_estabelecimento_w
from	divergencia_ordem_compra_item
where	nr_sequencia = nr_seq_regra_p;

select	coalesce(max(nr_sequencia),0)
into STRICT	nr_sequencia_w
from	regra_diverg_oc_nf
where	ie_tipo_regra = cd_tipo_divergencia_w
and	cd_estabelecimento = cd_estabelecimento_w;

if (ie_permite_justif_w = 'D') then

	select	count(*)
	into STRICT	qt_registros_w
	from	regra_diverg_oc_nf_just
	where	nr_seq_regra = nr_sequencia_w;

	if (qt_registros_w = 0) then
		ie_permite_justif_w := 'S';
	else
		select	count(*)
		into STRICT	qt_registros_w
		from	regra_diverg_oc_nf_just
		where	coalesce(cd_perfil_regra, cd_perfil_p)		= cd_perfil_p
		and	coalesce(cd_setor_regra, cd_setor_atendimento_p)	= cd_setor_atendimento_p
		and	coalesce(nm_usuario_regra, nm_usuario_p)		= nm_usuario_p
		and	coalesce(ie_permite_justif,'S') = 'N';

		if (qt_registros_w > 0) then
			ie_permite_justif_w := 'N';
		else
			ie_permite_justif_w := 'S';
		end if;
	end if;

end if;

return	ie_permite_justif_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
