CREATE OR REPLACE FUNCTION obter_se_exige_medico_conta ( cd_estabelecimento_p bigint, cd_convenio_p bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint) RETURNS varchar AS $body$
DECLARE



cd_grupo_w		bigint := 0;
cd_especialidade_w	bigint := 0;
cd_area_w		bigint := 0;
nr_sequencia_w		bigint;
ie_exige_medic_conta_w	varchar(5):='N';
cd_convenio_w		integer;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	regra_exige_medexec_conta a
	where	cd_estabelecimento				= cd_estabelecimento_p
	and	coalesce(cd_convenio,cd_convenio_w)			= cd_convenio_w
	and	coalesce(cd_procedimento,cd_procedimento_p)		= cd_procedimento_p
	and	coalesce(ie_origem_proced,ie_origem_proced_p)	= ie_origem_proced_p
	and	coalesce(a.cd_area_procedimento,cd_area_w)		= cd_area_w
	and	coalesce(a.cd_especialidade,cd_especialidade_w)	= cd_especialidade_w
	and	coalesce(a.cd_grupo_proc,cd_grupo_w)			= cd_grupo_w;




BEGIN

cd_convenio_w	:= coalesce(cd_convenio_p,0);
/* Obter Estrutura do procedimento */

begin
select 	cd_grupo_proc,
	cd_especialidade,
	cd_area_procedimento
into STRICT	cd_grupo_w,
	cd_especialidade_w,
	cd_area_w
from	Estrutura_Procedimento_V
where	cd_procedimento 	= cd_procedimento_p
and	ie_origem_proced	= ie_origem_proced_p;
exception
   	when others then
	begin
	cd_grupo_w		:= 0;
	cd_especialidade_w	:= 0;
	cd_area_w		:= 0;
	end;
end;

open C01;
loop
fetch C01 into
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	ie_exige_medic_conta_w	:= 'S';
	exit;
	end;
end loop;
close C01;

return	ie_exige_medic_conta_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
