CREATE OR REPLACE FUNCTION obter_se_exige_oc_nf ( cd_material_p bigint, cd_local_estoque_p bigint, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


cd_grupo_material_w		smallint;
cd_subgrupo_material_w		smallint;
cd_classe_material_w		integer;
ie_obriga_w			varchar(1) := 'N';
cd_local_estoque_w		smallint;

c01 CURSOR FOR
	SELECT	ie_obriga
	from	sup_regra_item_sem_oc_nf
	where	cd_estabelecimento = cd_estabelecimento_p
	and	((cd_local_estoque_w = 0) or (coalesce(cd_local_estoque::text, '') = '') or (coalesce(cd_local_estoque, cd_local_estoque_w) = cd_local_estoque_w))
	and	coalesce(cd_grupo_material, cd_grupo_material_w) = cd_grupo_material_w
	and	coalesce(cd_subgrupo_material, cd_subgrupo_material_w) = cd_subgrupo_material_w
	and	coalesce(cd_classe_material, cd_classe_material_w) = cd_classe_material_w
	and	coalesce(cd_material, cd_material_p) = cd_material_p
	order by
		coalesce(cd_material, 0),
		coalesce(cd_classe_material, 0),
		coalesce(cd_subgrupo_material, 0),
		coalesce(cd_grupo_material, 0),
		coalesce(cd_local_estoque, 0);



BEGIN

cd_local_estoque_w	:= coalesce(cd_local_estoque_p,0);
if (cd_material_p IS NOT NULL AND cd_material_p::text <> '') then
	begin

	select	cd_grupo_material,
		cd_subgrupo_material,
		cd_classe_material
	into STRICT	cd_grupo_material_w,
		cd_subgrupo_material_w,
		cd_classe_material_w
	from	estrutura_material_v
	where	cd_material = cd_material_p;


	OPEN c01;
	LOOP
	FETCH c01 INTO
		ie_obriga_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
	END LOOP;
	CLOSE 	c01;
	end;

end if;

return	ie_obriga_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
