CREATE OR REPLACE FUNCTION obter_se_exige_usu_compl_val (cd_convenio_p bigint, ie_tipo_atendimento_p bigint, ie_opcao_p text, ie_clinica_p bigint, cd_carater_internacao_p text, cd_perfil_p bigint, nr_seq_classificacao_p bigint) RETURNS varchar AS $body$
DECLARE


ie_exige_w		varchar(1);
ie_exige_cod_usuario_w	varchar(3);
ie_exige_complemento_w	varchar(3);
ie_exige_val_cart_w	varchar(3);

/*
Código usuário U
complemento C
Validade Carteira V
*/
BEGIN


if (coalesce(cd_convenio_p,0) > 0) then

	select	coalesce(max(ie_exige_cod_usuario),'X'),
		coalesce(max(ie_exige_complemento),'X'),
		coalesce(max(ie_exige_val_cart),'X')
	into STRICT	ie_exige_cod_usuario_w,
		ie_exige_complemento_w,
		ie_exige_val_cart_w
	from	regra_senha_convenio
	where	cd_convenio						    = cd_convenio_p
	and (coalesce(ie_tipo_atendimento, ie_tipo_atendimento_p) 	    = ie_tipo_atendimento_p)
	and (coalesce(cd_carater_internacao, coalesce(cd_carater_internacao_p,0)) = coalesce(cd_carater_internacao_p,0))
	and (coalesce(ie_clinica, coalesce(ie_clinica_p,0))			    = coalesce(ie_clinica_p,0))
	and (coalesce(nr_seq_classificacao,coalesce(nr_seq_classificacao_p,0))    = coalesce(nr_seq_classificacao_p,0))
	and (coalesce(cd_perfil,coalesce(cd_perfil_p,0)) 			    = coalesce(cd_perfil_p,0));

	if (ie_opcao_p = 'U') then
		ie_exige_w := ie_exige_cod_usuario_w;
	elsif (ie_opcao_p = 'C') then
		ie_exige_w := ie_exige_complemento_w;
	elsif (ie_opcao_p = 'V') then
		ie_exige_w := ie_exige_val_cart_w;
	end if;

end if;

return	ie_exige_w;

end;
$body$
