SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_existe_agenda_reg ( nr_seq_lista_espera_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno	varchar(2) := 'N';
nr_seq_regulacao_w 	bigint;
ie_tipo_w	varchar(10);
nr_seq_contrato_w	bigint;
qt_cota_w	bigint;


BEGIN

select	max(nr_seq_regulacao)
into STRICT	nr_seq_regulacao_w
from	agenda_lista_espera
where	nr_sequencia = nr_seq_lista_espera_p;

if (nr_seq_regulacao_w IS NOT NULL AND nr_seq_regulacao_w::text <> '') then
	select  max(ie_tipo)
	into STRICT	ie_tipo_w
	from 	regulacao_atend
	where	nr_sequencia = nr_seq_regulacao_w;
	
	
	select  max(nr_sequencia),
			max(qt_cota_atual)
	into STRICT 	nr_seq_contrato_w,
			qt_cota_w
	from    agenda_integrada_contrato
	where   ie_tipo = coalesce(ie_tipo_w,'XX')
	and     trunc(coalesce(dt_periodo_inicial,clock_timestamp())) <= trunc(clock_timestamp())
	and     trunc(coalesce(dt_periodo_final,clock_timestamp())) >= trunc(clock_timestamp());
	
end if;

if (coalesce(qt_cota_w,0) > 0)then
	ds_retorno := 'S';
end if;
	
return	ds_retorno;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_se_existe_agenda_reg ( nr_seq_lista_espera_p bigint) FROM PUBLIC;

