SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_existe_aval_tuber ( cd_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE


	qt_existe_w		bigint;
	ie_retorno_w	varchar(1);


BEGIN
ie_retorno_w	:= 'N';

SELECT count(*)
into STRICT qt_existe_w
FROM prescr_material a, prescr_medica b
WHERE a.nr_prescricao = b.nr_prescricao
AND   a.ie_agrupador  = 1
AND   (a.nr_seq_aval_tuber IS NOT NULL AND a.nr_seq_aval_tuber::text <> '')
AND   a.nr_prescricao = cd_prescricao_p;

if (qt_existe_w > 0) then
	ie_retorno_w	:= 'S';
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
