CREATE OR REPLACE FUNCTION obter_se_existe_ciclo_modulo ( nr_seq_modulo_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);


BEGIN

if (nr_seq_modulo_p IS NOT NULL AND nr_seq_modulo_p::text <> '') then

	SELECT obter_select_concatenado_bv('
					select	substr(obter_ds_classif_plano_atend(a.nr_seq_classificacao),1,250)
					from	mprev_plano_atendimento a,
							mprev_plano_atend_item b
					where	a.nr_seq_modulo = :nr_seq_modulo_p
					and		a.nr_sequencia = nr_seq_plano_atend(+)
					and		b.nr_sequencia is null','nr_seq_modulo_p='||nr_seq_modulo_p,', ') ds_retorno
	into STRICT	ds_retorno_w
	;

end if;

return	ds_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
