CREATE OR REPLACE FUNCTION obter_se_existe_cid ( nr_atendimento_p bigint, ie_tipo_diagnostico_p bigint) RETURNS varchar AS $body$
DECLARE


retorno_w varchar(1);
cont_w bigint;


BEGIN

select	count(t.cd_doenca)
into STRICT	cont_w
from	diagnostico_doenca t
where	t.nr_atendimento = nr_atendimento_p
and	t.ie_tipo_diagnostico = ie_tipo_diagnostico_p;

if (cont_w > 0) then
	retorno_w := 'S';
else
	retorno_w := 'N';
end if;

return retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
