CREATE OR REPLACE FUNCTION obter_se_existe_doc_vencido ( cd_cnpj_p text) RETURNS varchar AS $body$
DECLARE


ie_vencido_w			varchar(1) := 'N';
qt_existe_w			bigint;


BEGIN

select	count(*)
into STRICT	qt_existe_w
from	pessoa_juridica_doc
where	cd_cgc = cd_cnpj_p
and	trunc(dt_revisao,'dd') < trunc(clock_timestamp(),'dd')
and	coalesce(ie_consiste_cotacao,'S') = 'S';

if (qt_existe_w > 0) then
	ie_vencido_w := 'S';
end if;

return	ie_vencido_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
