SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_existe_doc_venc_cot ( cd_cnpj_p text, nr_cot_compra_p bigint) RETURNS varchar AS $body$
DECLARE


ie_vencido_w			varchar(1) := 'N';
qt_existe_w			bigint;
cd_material_w			bigint;
cd_grupo_material_w		bigint;
cd_subgrupo_material_w		bigint;
cd_classe_material_w		bigint;

c01 CURSOR FOR
SELECT	cd_material
from	cot_compra_item
where	nr_cot_compra = nr_cot_compra_p;


BEGIN

select	count(*)
into STRICT	qt_existe_w
from	pessoa_juridica_doc a,
	pessoa_juridica_doc_estrut b
where	a.nr_sequencia = b.nr_seq_documento
and	a.cd_cgc = cd_cnpj_p;

if (qt_existe_w = 0) then

	select	count(*)
	into STRICT	qt_existe_w
	from	pessoa_juridica_doc
	where	cd_cgc = cd_cnpj_p
	and	trunc(dt_revisao,'dd') < trunc(clock_timestamp(),'dd')
	and	coalesce(ie_consiste_cotacao,'S') = 'S';

	if (qt_existe_w > 0) then
		ie_vencido_w := 'S';
	end if;
else
	open C01;
	loop
	fetch C01 into
		cd_material_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

		select	a.cd_grupo_material,
			a.cd_subgrupo_material,
			a.cd_classe_material
		into STRICT	cd_grupo_material_w,
			cd_subgrupo_material_w,
			cd_classe_material_w
		from	estrutura_material_v a
		where	a.cd_material = cd_material_w;

		select	count(*)
		into STRICT	qt_existe_w
		from	pessoa_juridica_doc a,
			pessoa_juridica_doc_estrut b
		where	a.nr_sequencia = b.nr_seq_documento
		and	a.cd_cgc = cd_cnpj_p
		and	trunc(dt_revisao,'dd') < trunc(clock_timestamp(),'dd')
		and	coalesce(ie_consiste_cotacao,'S') = 'S'
		and	coalesce(cd_grupo_material, cd_grupo_material_w)		= cd_grupo_material_w
		and	coalesce(cd_subgrupo_material, cd_subgrupo_material_w)	= cd_subgrupo_material_w
		and	coalesce(cd_classe_material, cd_classe_material_w)		= cd_classe_material_w
		and (coalesce(cd_material, cd_material_w) 			= cd_material_w or cd_material_w = 0);

		if (qt_existe_w > 0) then
			ie_vencido_w := 'S';
			exit;
		end if;
		end;
	end loop;
	close C01;
end if;

return	ie_vencido_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
