CREATE OR REPLACE FUNCTION obter_se_existe_evento_agenda (cd_estabelecimento_p bigint, ie_evento_p text, ie_agenda_p text, ie_evolucao_clinica_p text default null, ie_clinica_p bigint default null) RETURNS varchar AS $body$
DECLARE


ie_evento_w	varchar(1);
ie_evolucao_clinica_w	varchar(40);
cd_perfil_w		bigint;
ie_clinica_w		bigint;

BEGIN
ie_evolucao_clinica_w	:= coalesce(ie_evolucao_clinica_p,'XPTO');
ie_clinica_w		:= coalesce(ie_clinica_p,0);
cd_perfil_w		:= obter_perfil_ativo;

if (coalesce(cd_estabelecimento_p,0) > 0) and (ie_evento_p IS NOT NULL AND ie_evento_p::text <> '') and (ie_agenda_p IS NOT NULL AND ie_agenda_p::text <> '') then
	select	CASE WHEN count(1)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_evento_w
	from	status_evento_agenda a
	where (a.cd_estabelecimento = cd_estabelecimento_p or exists (SELECT 1 
									from STATUS_EVENTO_AGENDA_ESTAB x 
									where x.cd_estabelecimento = cd_estabelecimento_p 
									and a.nr_sequencia 	   = x.nr_seq_status_evento_ag ))
	and	a.ie_evento = ie_evento_p 
	and	coalesce(a.ie_evolucao_clinica,ie_evolucao_clinica_w)	= ie_evolucao_clinica_w
	and	coalesce(a.cd_perfil,cd_perfil_w)	= cd_perfil_w
	and	((a.ie_agenda = ie_agenda_p) or (a.ie_agenda = 'T')) LIMIT 1;
end if;

return ie_evento_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
