CREATE OR REPLACE FUNCTION obter_se_existe_evol_laudo (nr_seq_laudo_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1);


BEGIN

SELECT	coalesce(max('S'),'N')
into STRICT	ds_retorno_w
FROM 	prescr_procedimento a,
		evolucao_paciente c,
		laudo_paciente b
WHERE	a.nr_sequencia	= b.nr_seq_prescricao
AND	a.nr_prescricao	= b.nr_prescricao
AND	c.nr_seq_int_prescr = a.nr_seq_interno
and c.nr_atendimento = b.nr_atendimento
AND	b.nr_sequencia	= nr_seq_laudo_p;


return	ds_retorno_w;

end;
