CREATE OR REPLACE FUNCTION obter_se_existe_exame_img_pacs ( nr_prescricao_p prescr_procedimento.nr_prescricao%TYPE, nr_seq_prescr_p prescr_procedimento.nr_sequencia%TYPE) RETURNS varchar AS $body$
DECLARE


qt_retorno_w	            integer;
ie_existe_exame             varchar(2) := 'N';
cd_tipo_procedimento_w      procedimento.cd_tipo_procedimento%TYPE;


BEGIN

    SELECT c.cd_tipo_procedimento
    INTO STRICT cd_tipo_procedimento_w
    FROM prescr_procedimento a, procedimento c
    WHERE a.cd_procedimento = c.cd_procedimento
        AND a.ie_origem_proced = c.ie_origem_proced
        AND a.nr_prescricao = nr_prescricao_p
        AND a.nr_sequencia = nr_seq_prescr_p;

    SELECT COUNT(*)
    INTO STRICT qt_retorno_w
    FROM prescr_procedimento a,
        prescr_procedimento_compl b,
        procedimento c
    WHERE b.nr_sequencia = a.nr_seq_proc_compl
        AND a.cd_procedimento = c.cd_procedimento
        AND a.ie_origem_proced = c.ie_origem_proced
        AND a.nr_prescricao = nr_prescricao_p
        AND c.cd_tipo_procedimento = cd_tipo_procedimento_w
        AND b.ie_imagem_relacionada_pacs = 'S';

    IF (qt_retorno_w > 1) THEN
        ie_existe_exame := 'S';
    END IF;

RETURN	ie_existe_exame;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
