CREATE OR REPLACE FUNCTION obter_se_existe_exam_same (cd_pessoa_fisica_p text, ie_tipo_local_p bigint) RETURNS varchar AS $body$
DECLARE


nr_registros_w 	bigint;
ie_existe_w	varchar(1);


BEGIN

ie_existe_w := 'N';

select	count(*)
into STRICT	nr_registros_w
from   	LAUDO_PACIENTE_LOC a,
	laudo_paciente b
where  	a.nr_sequencia = b.nr_sequencia
and	b.cd_pessoa_fisica = cd_pessoa_fisica_p
and    	a.ie_tipo_local = ie_tipo_local_p
and	a.dt_atualizacao = (SELECT max(x.dt_atualizacao) from laudo_paciente_loc x where x.nr_sequencia = a.nr_sequencia);

if (nr_registros_w > 0) then
	begin
	ie_existe_w := 'S';
	end;
end if;

return	ie_existe_w;

end;
$body$
