CREATE OR REPLACE FUNCTION obter_se_existe_fornec_cot ( nr_cot_compra_p bigint, cd_fornecedor_p text, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE

 
ie_cod_fornec_integracao_w		parametro_compras.ie_cod_fornec_integracao%type;			
qt_registros_w				bigint;
ie_existe_w				varchar(1) := 'N';
cd_cnpj_w				pessoa_juridica.cd_cgc%type;
			

BEGIN 
 
select	coalesce(max(ie_cod_fornec_integracao),'CNPJ') 
into STRICT	ie_cod_fornec_integracao_w 
from	parametro_compras 
where	cd_estabelecimento = cd_estabelecimento_p;
 
if (ie_cod_fornec_integracao_w = 'CNPJ') then 
	cd_cnpj_w := cd_fornecedor_p;	
elsif (ie_cod_fornec_integracao_w = 'RFC') then 
	select	max(cd_cgc) 
	into STRICT	cd_cnpj_w 
	from	pessoa_juridica 
	where	cd_rfc = cd_fornecedor_p 
	and	ie_situacao = 'A';
elsif (ie_cod_fornec_integracao_w = 'CURP') then 
	select	max(cd_cgc) 
	into STRICT	cd_cnpj_w 
	from	pessoa_juridica 
	where	cd_curp = cd_fornecedor_p 
	and	ie_situacao = 'A';
end if;
 
select	count(*) 
into STRICT	qt_registros_w 
from	cot_compra_forn 
where	nr_cot_compra = nr_cot_compra_p 
and	cd_cgc_fornecedor = cd_cnpj_w;
 
if (qt_registros_w > 0) then 
	ie_existe_w := 'S';
end if;
 
 
return	ie_existe_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
