CREATE OR REPLACE FUNCTION obter_se_existe_issue_aberta ( nr_story_p bigint, nr_sprint_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w	varchar(1);


BEGIN

select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
into STRICT	ie_retorno_w
from	desenv_story a,
	desenv_story_sprint b
where	a.nr_story = nr_story_p
and	b.nr_sprint = nr_sprint_p
and	a.nr_sequencia = b.nr_story
and	a.cd_tipo = 'IS'
and	coalesce(b.cd_status,0) <> 4;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
