CREATE OR REPLACE FUNCTION obter_se_existe_item_nao_ass (nr_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE


ie_item_nao_assinado_w	varchar(1);


BEGIN

select	coalesce(max('S'),'N')
into STRICT	ie_item_nao_assinado_w
from	cpoe_material a,
		prescr_material b
where	a.nr_sequencia = b.nr_seq_mat_cpoe
and		b.nr_prescricao = nr_prescricao_p
and		coalesce(obter_data_assinatura_digital(a.nr_seq_assinatura)::text, '') = ''
and		coalesce(a.nr_seq_procedimento::text, '') = ''
and		coalesce(a.nr_seq_hemoterapia::text, '') = '';

if (ie_item_nao_assinado_w = 'N') then
	select	coalesce(max('S'),'N')
	into STRICT	ie_item_nao_assinado_w
	from	cpoe_procedimento a,
			prescr_procedimento b
	where	a.nr_sequencia = b.nr_seq_proc_cpoe
	and		b.nr_prescricao = nr_prescricao_p
	and		coalesce(obter_data_assinatura_digital(a.nr_seq_assinatura)::text, '') = '';
	
	if (ie_item_nao_assinado_w = 'N') then
		select	coalesce(max('S'),'N')
		into STRICT	ie_item_nao_assinado_w
		from	cpoe_gasoterapia a,
				prescr_gasoterapia b
		where	a.nr_sequencia = b.nr_seq_gas_cpoe
		and		b.nr_prescricao = nr_prescricao_p
		and		coalesce(obter_data_assinatura_digital(a.nr_seq_assinatura)::text, '') = '';
		
		if (ie_item_nao_assinado_w = 'N') then
			select	coalesce(max('S'),'N')
			into STRICT	ie_item_nao_assinado_w
			from	cpoe_recomendacao a,
					prescr_recomendacao b
			where	a.nr_sequencia = b.nr_seq_rec_cpoe
			and		b.nr_prescricao = nr_prescricao_p
			and		coalesce(obter_data_assinatura_digital(a.nr_seq_assinatura)::text, '') = '';
		
			if (ie_item_nao_assinado_w = 'N') then
				select	coalesce(max('S'),'N')
				into STRICT	ie_item_nao_assinado_w
				from	cpoe_dieta a,
						prescr_dieta b
				where	a.nr_sequencia = b.nr_seq_dieta_cpoe
				and		b.nr_prescricao = nr_prescricao_p
				and		coalesce(obter_data_assinatura_digital(a.nr_seq_assinatura)::text, '') = '';
				
				if (ie_item_nao_assinado_w = 'N') then
					select	coalesce(max('S'),'N')
					into STRICT	ie_item_nao_assinado_w
					from	cpoe_dieta a,
							prescr_material b
					where	a.nr_sequencia = b.nr_seq_dieta_cpoe
					and		b.nr_prescricao = nr_prescricao_p
					and		coalesce(obter_data_assinatura_digital(a.nr_seq_assinatura)::text, '') = '';
					
					if (ie_item_nao_assinado_w = 'N') then
						select	coalesce(max('S'),'N')
						into STRICT	ie_item_nao_assinado_w
						from	cpoe_dieta a,
								rep_jejum b
						where	a.nr_sequencia = b.nr_seq_dieta_cpoe
						and		b.nr_prescricao = nr_prescricao_p
						and		coalesce(obter_data_assinatura_digital(a.nr_seq_assinatura)::text, '') = '';
						
						if (ie_item_nao_assinado_w = 'N') then
							select	coalesce(max('S'),'N')
							into STRICT	ie_item_nao_assinado_w
							from	cpoe_dialise a,
									prescr_solucao b
							where	a.nr_sequencia = b.nr_seq_dialise_cpoe
							and		b.nr_prescricao = nr_prescricao_p
							and		coalesce(obter_data_assinatura_digital(a.nr_seq_assinatura)::text, '') = '';
							
							if (ie_item_nao_assinado_w = 'N') then
								select	coalesce(max('S'),'N')
								into STRICT	ie_item_nao_assinado_w
								from	cpoe_hemoterapia a,
										prescr_procedimento b
								where	a.nr_sequencia = b.nr_seq_proc_cpoe
								and		b.nr_prescricao = nr_prescricao_p
								and		coalesce(obter_data_assinatura_digital(a.nr_seq_assinatura)::text, '') = '';
							end if;
						end if;
					end if;
				end if;
			end if;
		end if;
	end if;
end if;

return	ie_item_nao_assinado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
