CREATE OR REPLACE FUNCTION obter_se_existe_itens_prot_onc ( nr_seq_paciente_p bigint) RETURNS varchar AS $body$
DECLARE


qt_itens_w		bigint := 0;


BEGIN

select	count(*)
into STRICT	qt_itens_w
from	paciente_protocolo_medic
where	nr_seq_paciente = nr_seq_paciente_p;

if (qt_itens_w = 0) then
	select	count(*)
	into STRICT	qt_itens_w
	from	paciente_protocolo_soluc
	where	nr_seq_paciente = nr_seq_paciente_p;
end if;

if (qt_itens_w = 0) then
	select	count(*)
	into STRICT	qt_itens_w
	from	paciente_protocolo_proc
	where	nr_seq_paciente = nr_seq_paciente_p;
end if;

if (qt_itens_w = 0) then
	select	count(*)
	into STRICT	qt_itens_w
	from	paciente_protocolo_rec
	where	nr_seq_paciente = nr_seq_paciente_p;
end if;

if (qt_itens_w = 0) then
	return	'N';
else
	return	'S';
end if;

end;
$body$
