CREATE OR REPLACE FUNCTION obter_se_existe_itens_zerado ( nr_sequencia_p bigint, ie_permite_lib_item_zerado_p text) RETURNS varchar AS $body$
DECLARE


ie_existe_w	varchar(1) := 'N';
nr_seq_item_w	bigint;
ie_opcao_w	varchar(1);

C01 CURSOR FOR
	SELECT	nr_seq_matpaci,
		'M' ie_opcao
	from	auditoria_matpaci
	where	nr_seq_auditoria = nr_sequencia_p
	
union all

	SELECT	nr_seq_propaci,
		'P' ie_opcao
	from	auditoria_propaci
	where	nr_seq_auditoria = nr_sequencia_p
	and 	ie_permite_lib_item_zerado_p <> 'M';


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_item_w,
	ie_opcao_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if (ie_opcao_w = 'M') then
		select	coalesce(max('S'),'N')
		into STRICT	ie_existe_w
		from	material_atend_paciente
		where	nr_sequencia = nr_seq_item_w
		and	vl_material = 0;

		if (ie_existe_w = 'S') then
			exit;
		end if;
	end if;

	if (ie_opcao_w = 'P') then
		select	coalesce(max('S'),'N')
		into STRICT	ie_existe_w
		from	procedimento_paciente
		where	nr_sequencia = nr_seq_item_w
		and	vl_procedimento = 0;

		if (ie_existe_w = 'S') then
			exit;
		end if;
	end if;
	end;
end loop;
close C01;

return	ie_existe_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
