CREATE OR REPLACE FUNCTION obter_se_existe_marca_mat ( cd_material_p bigint, ds_marca_p text) RETURNS varchar AS $body$
DECLARE


nr_seq_marca_w		bigint;
qt_existe_w		bigint;
ie_existe_w		varchar(1) := 'N';
ds_marca_w		varchar(255);


BEGIN

ds_marca_w := substr(coalesce(substr(ds_marca_p,1,position('(' in ds_marca_p)-1),ds_marca_p),1,255);

select	coalesce(max(a.nr_sequencia),0)
into STRICT	nr_seq_marca_w
from	marca a
where	somente_letra_espaco_trim(upper(substr(coalesce(substr(a.ds_marca,1,position('(' in a.ds_marca)-1),a.ds_marca),1,255))) = somente_letra_espaco_trim(upper(substr(coalesce(substr(ds_marca_w,1,position('(' in ds_marca_w)-1),ds_marca_w),1,255)))
and exists (
	SELECT	1
	from	material_marca b
	where	b.nr_sequencia = a.nr_sequencia
	and	b.cd_material = cd_material_p);

if (nr_seq_marca_w = 0) then

	select	coalesce(max(a.nr_sequencia),0)
	into STRICT	nr_seq_marca_w
	from	marca a
	where	somente_letra_espaco_trim(upper(substr(coalesce(substr(a.ds_marca,1,position('(' in a.ds_marca)-1),a.ds_marca),1,255))) like '%'|| somente_letra_espaco_trim(upper(substr(coalesce(substr(ds_marca_w,1,position('(' in ds_marca_w)-1),ds_marca_w),1,255))) ||'%'
	and exists (
		SELECT	1
		from	material_marca b
		where	b.nr_sequencia = a.nr_sequencia
		and	b.cd_material = cd_material_p);
end if;

if (nr_seq_marca_w > 0) then

	select	count(*)
	into STRICT	qt_existe_w
	from	material_marca
	where	cd_material	= cd_material_p
	and	nr_sequencia	= nr_seq_marca_w;

	if (qt_existe_w > 0) then
		ie_existe_w := 'S';
	end if;
end if;

return	ie_existe_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
