CREATE OR REPLACE FUNCTION obter_se_existe_medico_import (nr_crm_p text, uf_crm_p text) RETURNS varchar AS $body$
DECLARE


ie_existe_w	varchar(1);
nr_aux_w		bigint;

BEGIN

select	count(*)
into STRICT	nr_aux_w
from	cadastro_medico
where	nr_crm = nr_crm_p
and	upper(uf_crm) = upper(uf_crm_p);

if (nr_aux_w > 0) then
	ie_existe_w := 'S';
else
	ie_existe_w := 'N';
end if;


return	ie_existe_w;

end;
