SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_existe_medic_fim_adm ( nr_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE



ds_medicamento_w	varchar(255);
nr_seq_atendimento_w	bigint;
ds_retorno_w		varchar(2000);

C01 CURSOR FOR
	SELECT 	SUBSTR(obter_desc_material(CD_MATERIAL),1,60)
	FROM   	paciente_atend_medic a,
		PACIENTE_ATEND_MEDIC_ADM x
	WHERE  	a.nr_seq_atendimento 	= x.nr_seq_atendimento
	AND	x.NR_SEQ_ATENDIMENTO 	= nr_seq_atendimento_w
	AND	a.nr_seq_material	= x.nr_seq_material
  and substr(obter_se_medic_quimio(CD_MATERIAL),1,1) = 'S'
	AND     coalesce(x.DT_FIM_ADMINISTRACAO::text, '') = '';



BEGIN

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') then

	ds_retorno_w := '';

	SELECT 	max(a.nr_seq_atendimento)
	INTO STRICT	nr_seq_atendimento_w
	FROM	can_ordem_prod a,
		prescr_medica b,
		prescr_material c
	WHERE 	a.nr_prescricao 	= b.nr_prescricao
	AND	a.nr_sequencia 		= c.nr_seq_ordem_prod
	AND	a.nr_prescricao 	= nr_prescricao_p
	AND	a.ie_cancelada 		= 'N'
	AND	coalesce(c.nr_sequencia_diluicao::text, '') = ''
	AND	(a.dt_checagem IS NOT NULL AND a.dt_checagem::text <> '')
	AND	(a.dt_fim_preparo IS NOT NULL AND a.dt_fim_preparo::text <> '')
	AND	((c.ie_se_necessario = 'N') OR (c.ie_urgencia = 'S'))
	AND	c.ie_agrupador = 1
	AND a.ie_cancelada 	= 'N';

	open C01;
	loop
	fetch C01 into
		ds_medicamento_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		ds_retorno_w := substr(ds_retorno_w || '#;#'||ds_medicamento_w,1,2000);
		end;
	end loop;
	close C01;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_se_existe_medic_fim_adm ( nr_prescricao_p bigint) FROM PUBLIC;

