CREATE OR REPLACE FUNCTION obter_se_existe_mes (cd_empresa_p bigint, dt_referencia_p timestamp) RETURNS varchar AS $body$
DECLARE

ds_retorno_w	varchar(1) := 'N';
qt_registros_w	bigint;

BEGIN


select 	count(*)
into STRICT	qt_registros_w
from	ctb_mes_ref
where	cd_empresa 	= cd_empresa_p
and	dt_referencia 	= dt_referencia_p;

if (qt_registros_w > 0 ) then
	ds_retorno_w	:= 'S';
end if;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
