CREATE OR REPLACE FUNCTION obter_se_existe_morf_peca ( nr_prescricao_p bigint, nr_seq_prescr_p bigint, nr_seq_laudo_p bigint ) RETURNS varchar AS $body$
DECLARE


ie_result_w		varchar(1) := 'S';
nr_seq_prescr_peca_w	bigint;
cd_morfologia_w		varchar(10);

c01 CURSOR FOR
	SELECT	p.nr_sequencia,
		t.cd_morfologia
	from	prescr_proc_peca p,
		tipo_amostra_pato_morf t
	where	p.nr_prescricao = nr_prescricao_p
	and 	p.nr_seq_prescr = nr_seq_prescr_p
	and     coalesce(p.nr_seq_peca::text, '') = ''
	and     p.nr_seq_amostra_princ 	= t.nr_seq_tipo_amostra_pato
	and	p.nr_sequencia_princ = (SELECT 	min(nr_sequencia_princ)
					from 	prescr_proc_peca
					where 	nr_prescricao = nr_prescricao_p
					and	nr_seq_prescr = nr_seq_prescr_p)
	order 	by nr_sequencia;


BEGIN

open c01;
loop
fetch c01 into
	nr_seq_prescr_peca_w,
	cd_morfologia_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	select 	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_result_w
	from	laudo_cido_morfologia
	where	nr_seq_laudo 		= nr_seq_laudo_p
	and	nr_seq_prescr_peca 	= nr_seq_prescr_peca_w
	and	cd_morfologia		= cd_morfologia_w;

	if (ie_result_w = 'S') then --tbschulz OS346716 - se possuir uma morfologia da peça principal já permite liberação
		exit;
	end if;

	end;
end loop;
close c01;

return	ie_result_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
