SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_existe_obj_java ( cd_funcao_p bigint, ie_opcao_p text, ds_objeto_p text, nm_objeto_p text, nm_objeto_pai_p text, nm_tabela_p text, ie_tipo_coluna_p text, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


qt_reg_w		bigint;
ds_retorno_w 	varchar(1) := 'N';


BEGIN

if (ie_opcao_p = 'WPageControl') then

	select  count(*)
	into STRICT	qt_reg_w
	from    dic_objeto a
	where   a.ie_tipo_objeto = 'P'
	and     coalesce(a.nr_seq_obj_sup::text, '') = ''
	and     a.cd_funcao = cd_funcao_p
	and 	upper(replace(a.ds_texto,' ','')) =  upper(replace(ds_objeto_p,' ',''))  LIMIT 1;

elsif (ie_opcao_p = 'TTabSheet') or (ie_opcao_p = 'TS') then

		select  count(*)
		into STRICT	qt_reg_w
		from    dic_objeto a
		where   a.ie_tipo_objeto = 'P'
		and     a.cd_funcao = cd_funcao_p
		and 	upper(replace(a.ds_texto,' ','')) =  upper(replace(ds_objeto_p,' ',''))  LIMIT 1;

elsif (ie_opcao_p = 'TPopUpMenu') or (ie_opcao_p = 'PM') then

		select  count(*)
		into STRICT	qt_reg_w
		from    dic_objeto a
		where   a.ie_tipo_objeto = 'M'
		and     a.cd_funcao = cd_funcao_p
		and 	replace(upper(a.nm_objeto),'WPUMC','') =  replace(replace(upper(nm_objeto_p),'_PM',''),'_','')  LIMIT 1;

elsif (ie_opcao_p = 'TMenuItem') or (ie_opcao_p = 'MI') then

		select  count(*)
		into STRICT	qt_reg_w
		from    dic_objeto a
		where   a.ie_tipo_objeto = 'MI'
		and	    a.cd_funcao = cd_funcao_p
		and 	upper(replace(a.ds_texto,' ','')) =  upper(replace(ds_objeto_p,' ',''))  LIMIT 1;

elsif (ie_opcao_p = 'TWCPanel') or (ie_opcao_p = 'WCP') then

		if (coalesce(trim(both ds_objeto_p)::text, '') = '') then

			select  count(*)
			into STRICT	qt_reg_w
			from    dic_objeto a
			where   a.ie_tipo_objeto = 'C'
			and	    a.cd_funcao = cd_funcao_p
			and		replace(replace(upper(elimina_numeros(a.nm_objeto)),'WCP',''),'_','') = replace(replace(upper(elimina_numeros(nm_objeto_p)),'_WCP',''),'_','')  LIMIT 1;

		else

			select  count(*)
			into STRICT	qt_reg_w
			from    dic_objeto a
			where   a.ie_tipo_objeto = 'C'
			and	    a.cd_funcao = cd_funcao_p
			and 	upper(elimina_numeros(replace(coalesce(obter_desc_expressao(a.cd_exp_texto), a.ds_texto),' ',''))) =  upper(elimina_numeros(replace(ds_objeto_p,' ','')))  LIMIT 1;

		end if;


elsif (ie_opcao_p = 'AC') then

		select  count(*)
		into STRICT	qt_reg_w
		from    dic_objeto a
		where   a.ie_tipo_objeto in ('AC','F')
		and	    a.cd_funcao = cd_funcao_p
		and 	upper(replace(coalesce(obter_desc_expressao(CD_EXP_CAMPO_TELA, NM_CAMPO_TELA), a.ds_texto),' ','')) =  upper(replace(ds_objeto_p,' ',''))
		and 	upper(elimina_numeros(replace(obter_nome_obj_dic_obj(a.nr_seq_obj_sup),'_',''))) =  upper(elimina_numeros(replace(nm_objeto_pai_p,'_','')))  LIMIT 1;

elsif (ie_opcao_p = 'DC') then

		select	count(*)
		into STRICT	qt_reg_w
		from	w_colunas_grid_wcpanel a
		where	a.ie_tipo = 'BJ'
		and		a.cd_funcao = cd_funcao_p
		and		a.nm_usuario = nm_usuario_p
		and		upper(a.nm_tabela) = upper(nm_tabela_p)
		and		upper(a.nm_coluna) = upper(nm_objeto_p)
		and		upper(a.ie_tipo_coluna)	=	upper(ie_tipo_coluna_p)
		and		upper(replace(a.ds_titulo_coluna,' ','')) = upper(replace(ds_objeto_p,' ',''))
		and		replace(replace(upper(elimina_numeros(a.nm_componente)),'WDBP',''),'_','') = replace(replace(upper(elimina_numeros(nm_objeto_pai_p)),'_DBP',''),'_','')  LIMIT 1;

elsif (ie_opcao_p	= 'TWDLGPanel') then

		select  count(*)
		into STRICT	qt_reg_w
		from    w_colunas_grid_wcpanel a
		where   a.ie_tipo = 'WJ'
		and	    a.cd_funcao = cd_funcao_p
		and		a.nm_usuario = nm_usuario_p
		and		replace(replace(replace(upper(elimina_numeros(a.nm_componente)),'WDLGP',''),'WDLG',''),'_','') = replace(replace(upper(elimina_numeros(nm_objeto_p)),'_WDLG',''),'_','')  LIMIT 1;

elsif (ie_opcao_p = 'TDBPanel') then

		select  count(*)
		into STRICT	qt_reg_w
		from    w_colunas_grid_wcpanel a
		where   a.ie_tipo = 'BJ'
		and	    a.cd_funcao = cd_funcao_p
		and		a.nm_usuario = nm_usuario_p
		and		replace(replace(upper(elimina_numeros(a.nm_componente)),'WDBP',''),'_','') = replace(replace(upper(elimina_numeros(nm_objeto_p)),'_DBP',''),'_','')  LIMIT 1;

elsif (ie_opcao_p = 'DBPANEL_CONTROLE') then

		select  count(*)
		into STRICT	qt_reg_w
		from    w_colunas_grid_wcpanel a
		where   a.ie_tipo = 'BJ'
		and	    a.cd_funcao = cd_funcao_p
		and		a.nm_usuario = nm_usuario_p
		and		replace(replace(upper(elimina_numeros(a.nm_componente)),'WDBP',''),'_','') = replace(replace(upper(elimina_numeros(nm_objeto_pai_p)),'_DBP',''),'_','')  LIMIT 1;

elsif (ie_opcao_p = 'WCPANEL_CONTROLE') then

		select  count(*)
		into STRICT	qt_reg_w
		from    dic_objeto a
		where   a.ie_tipo_objeto = 'C'
		and	    a.cd_funcao = cd_funcao_p
		and		replace(replace(upper(elimina_numeros(a.nm_objeto)),'WCP',''),'_','') = replace(replace(upper(elimina_numeros(nm_objeto_pai_p)),'_WCP',''),'_','')  LIMIT 1;

end if;

if (qt_reg_w = 0 ) then

	ds_retorno_w := 'S';

end if;


return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
