CREATE OR REPLACE FUNCTION obter_se_existe_ordem_cot ( nr_cot_compra_p bigint) RETURNS varchar AS $body$
DECLARE

 
qt_existe_w		bigint;
nr_ordem_compra_w	bigint;
ie_retorno_w		varchar(1);


BEGIN 
 
select	count(*) 
into STRICT	qt_existe_w 
from	ordem_compra_item 
where	nr_ordem_compra = nr_cot_compra_p;
 
if (qt_existe_w = 0) then 
	begin 
 
	select	coalesce(max(nr_ordem_compra),0) 
	into STRICT	nr_ordem_compra_w 
	from	cot_compra_resumo_v 
	where	nr_cot_compra = nr_cot_compra_p;
 
	if (nr_ordem_compra_w > 0) then 
		qt_existe_w	:= 1;
	end if;
 
	end;
end if;
 
if (qt_existe_w > 0) then 
	ie_retorno_w	:= 'S';
else 
	ie_retorno_w	:= 'N';
end if;
 
return ie_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
