CREATE OR REPLACE FUNCTION obter_se_existe_prescr_hemo ( nr_seq_derivado_p bigint, nr_atendimento_p bigint, nr_prescricao_p bigint, cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE

ie_verifica_w	bigint;
ds_retorno_w	varchar(1) := 'N';

BEGIN

select	count(*)
into STRICT	ie_verifica_w
from	prescr_medica b,
	prescr_procedimento a
where	a.nr_prescricao = b.nr_prescricao
and 	b.nr_prescricao <> nr_prescricao_p
and 	b.nr_atendimento = nr_atendimento_p
and 	((coalesce(a.ie_filtrado,'N')  = 'S') or (coalesce(a.ie_irradiado,'N')  = 'S') or (coalesce(a.ie_aliquotado,'N') = 'S') or (coalesce(a.ie_lavado,'N') = 'S') or (coalesce(a.ie_aferese,'N') = 'S') or (coalesce(a.ie_pool,'N') = 'S'))
and 	a.nr_seq_derivado = nr_seq_derivado_p;

if (ie_verifica_w = 0) then
	select	count(*)
	into STRICT	ie_verifica_w
	from	prescr_medica b,
		prescr_procedimento a
	where	a.nr_prescricao = b.nr_prescricao
	and 	b.nr_prescricao <> nr_prescricao_p
	and	b.nr_atendimento <> nr_atendimento_p
	and	b.cd_pessoa_fisica = cd_pessoa_fisica_p
	and 	((coalesce(a.ie_filtrado,'N')  = 'S') or (coalesce(a.ie_irradiado,'N')  = 'S') or (coalesce(a.ie_aliquotado,'N') = 'S') or (coalesce(a.ie_lavado,'N') = 'S') or (coalesce(a.ie_aferese,'N') = 'S') or (coalesce(a.ie_pool,'N') = 'S'))
	and 	a.nr_seq_derivado = nr_seq_derivado_p;
end if;

if (ie_verifica_w > 0) then
	ds_retorno_w := 'S';
end if;

return ds_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
