SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_existe_prot_prescr ( cd_protocolo_p bigint, nr_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE

ie_existe_prot_w	varchar(1) := 'N';

BEGIN
if (cd_protocolo_p IS NOT NULL AND cd_protocolo_p::text <> '') and (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') then
	/* Verifica se existe o protocolo no cadastro da prescrição */

	select	coalesce(max('S'),'N')
	into STRICT		ie_existe_prot_w
	from		prescr_medica
	where	nr_prescricao	= nr_prescricao_p
	and		cd_protocolo	= cd_protocolo_p;
	/* Se não existe na prescrição verifica nos materiais/medicamentos da prescrição */

	if (ie_existe_prot_w = 'N') then
		select	coalesce(max('S'),'N')
		into STRICT		ie_existe_prot_w
		from		prescr_material
		where	nr_prescricao	= nr_prescricao_p
		and		cd_protocolo	= cd_protocolo_p;
		/* Se não existe nos materiais/medicamentos verifica nos procedimentos */

		if (ie_existe_prot_w = 'N') then
			select	coalesce(max('S'),'N')
			into STRICT		ie_existe_prot_w
			from		prescr_procedimento
			where	nr_prescricao	= nr_prescricao_p
			and		cd_protocolo	= cd_protocolo_p;
		end if;
	end if;
end if;

return ie_existe_prot_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
