CREATE OR REPLACE FUNCTION obter_se_existe_sc_na_oc ( nr_solic_compra_p bigint, nr_ordem_compra_p bigint) RETURNS varchar AS $body$
DECLARE


ie_existe_w			varchar(1) := 'N';
qt_existe_w			integer;


BEGIN

select	count(*)
into STRICT	qt_existe_w
from	ordem_compra_item
where	nr_ordem_compra = nr_ordem_compra_p
and	nr_solic_compra = nr_solic_compra_p;

if (qt_existe_w > 0) then
	ie_existe_w := 'S';
end if;

return	ie_existe_w;

end;
$body$
