CREATE OR REPLACE FUNCTION obter_se_existe_segurado_lib ( nr_seq_lote_reajuste_p bigint) RETURNS varchar AS $body$
DECLARE


qt_existe_w	bigint;
ds_retorno_w	varchar(1);


BEGIN

select	count(*)
into STRICT	qt_existe_w
from	pls_segurado_preco
where	nr_seq_lote_reajuste = nr_seq_lote_reajuste_p
and	coalesce(dt_liberacao::text, '') = '';

if (qt_existe_w > 0) then
	ds_retorno_w := 'S';
else
	ds_retorno_w := 'N';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
