SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_existe_soap_perfil (cd_perfil_p bigint) RETURNS varchar AS $body$
DECLARE


ie_soap_w				varchar(1) := 'S';
ie_cadastro_cliente_w	varchar(1);


BEGIN

if (cd_perfil_p IS NOT NULL AND cd_perfil_p::text <> '') then


	select	coalesce(max('S'), 'N')
	into STRICT	ie_cadastro_cliente_w
	from	perfil_item_pront a,
			prontuario_item_soap_cli b
	where	b.nr_seq_item_prontuario = a.nr_sequencia
	and		a.cd_perfil = cd_perfil_p;

	if ( ie_cadastro_cliente_w = 'S') then

		select	coalesce(max('S'), 'N')
		into STRICT	ie_soap_w
		from	perfil_item_pront a,
				prontuario_item_soap_cli b
		where	b.nr_seq_item_prontuario = a.nr_sequencia
		and		a.cd_perfil = cd_perfil_p
		and		ie_etapa_soap <> 'T';

	end if;


end if;

return ie_soap_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
