CREATE OR REPLACE FUNCTION obter_se_existe_solic_cotacao ( nr_cot_compra_p bigint) RETURNS varchar AS $body$
DECLARE


ie_existe_w			varchar(1) := 'N';
qt_existe_w			integer;


BEGIN
select	count(*)
into STRICT	qt_existe_w
from (
	SELECT	1
	from	cot_compra_item
	where	nr_cot_compra = nr_cot_compra_p
	and	(nr_solic_compra IS NOT NULL AND nr_solic_compra::text <> '')
	
union

	SELECT	1
	from	cot_compra_solic_agrup
	where	nr_cot_compra = nr_cot_compra_p
	and	(nr_solic_compra IS NOT NULL AND nr_solic_compra::text <> '')) alias3;

if (qt_existe_w > 0) then
	ie_existe_w := 'S';
end if;

return	ie_existe_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
