SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_existe_tit_status (nr_atendimento_p bigint, ie_status_p text, ie_tipo_convenio_p bigint) RETURNS varchar AS $body$
DECLARE

 
qt_titulos_w 	bigint;
ie_existe_w		varchar(1) := 'N';
ie_tipo_convenio_w integer;

				 

BEGIN 
 
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') and (ie_status_p IS NOT NULL AND ie_status_p::text <> '') and (ie_tipo_convenio_p IS NOT NULL AND ie_tipo_convenio_p::text <> '') then 
begin 
 
	select Obter_Tipo_Convenio(obter_convenio_atendimento(nr_atendimento_p)) 
	into STRICT	ie_tipo_convenio_w 
	;
 
	if (ie_tipo_convenio_w = 1) then 
		begin 
		SELECT 	COUNT(*) 
		into STRICT	qt_titulos_w 
		FROM  	atendimento_paciente a, 
				conta_paciente b, 
				titulo_receber c 
		WHERE 	a.nr_atendimento = b.nr_atendimento 
		AND	  	b.nr_interno_conta = c.nr_interno_conta 
		and	  	a.nr_atendimento = nr_atendimento_p;
 
		if (qt_titulos_w > 0) then 
 
			SELECT 	COUNT(*) 
			into STRICT	qt_titulos_w 
			FROM  	atendimento_paciente a, 
					conta_paciente b, 
					titulo_receber c 
			WHERE 	a.nr_atendimento = b.nr_atendimento 
			AND	  	b.nr_interno_conta = c.nr_interno_conta 
			and	  	a.nr_atendimento = nr_atendimento_p 
			and		c.ie_situacao = ie_status_p 
			and		ie_tipo_convenio_w = ie_tipo_convenio_p;
			 
			if (qt_titulos_w > 0) then 
				ie_existe_w := 'S';
			end if;
			 
		else	 
			ie_existe_w := 'S';
		end if;
		end;
	end if;
end;
end if;
return	ie_existe_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
