SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_faixa_etaria_pac ( cd_pessoa_fisica_p text, cd_setor_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


qt_idade_w		bigint;
ie_idade_min_w		bigint;
ie_idade_max_w		bigint;
ie_cadastro_w		bigint;
ie_permite_w		varchar(1) := 'S';
qt_idade_min_w		bigint;
qt_idade_max_w		bigint;
qt_idade_atend_w	bigint;
nr_sequencia_w		bigint;
nr_sequencia_ww		bigint;
C01 CURSOR FOR
	SELECT	qt_idade_min,
		qt_idade_max
	from	faixa_etaria_pac
	where	ie_situacao = 'A';


BEGIN

select	count(*)
into STRICT	ie_cadastro_w
from	faixa_etaria_pac
where	ie_situacao = 'A';

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') and (coalesce(ie_cadastro_w,0) > 0) then

	begin
	select	coalesce(obter_idade_pf(cd_pessoa_fisica_p, clock_timestamp(), 'A'),0)
	into STRICT	qt_idade_w
	;

	select	max(nr_sequencia)
	into STRICT	nr_sequencia_w
	from	faixa_etaria_pac
	where	qt_idade_w between qt_idade_min and	qt_idade_max
	and	ie_situacao = 'A';

	exception
	when others then

		qt_idade_w := 0;
	end;


	if (coalesce(qt_idade_w,0) > 0) then

		begin

		select	coalesce(max(obter_idade_pf(obter_pessoa_atendimento(nr_atendimento,'C'), clock_timestamp(), 'A')),0)
		into STRICT	qt_idade_atend_w
		from	unidade_atendimento
		where	cd_setor_atendimento = cd_setor_atendimento_p
		and	dt_entrada_unidade = 	(	SELECT	max(dt_entrada_unidade)
							from	unidade_atendimento
							where	cd_setor_atendimento = cd_setor_atendimento_p
							and	(nr_atendimento IS NOT NULL AND nr_atendimento::text <> '')
							and	coalesce(ie_controle_faixa_etaria,'N') = 'S'
						);

		select	max(nr_sequencia)
		into STRICT	nr_sequencia_ww
		from	faixa_etaria_pac
		where	qt_idade_atend_w between qt_idade_min and qt_idade_max
		and	ie_situacao = 'A';

		exception
		when others then

			qt_idade_atend_w := 0;
		end;
		if (coalesce(qt_idade_atend_w,0) > 0) then
			if (coalesce(nr_sequencia_w,0) <> coalesce(nr_sequencia_ww,0)) then
				ie_permite_w := 'N';
			end if;
		end if;

	end if;

end if;

return	coalesce(ie_permite_w,'S');

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
