CREATE OR REPLACE FUNCTION obter_se_faturamento_direto ( nr_seq_material_p bigint, nr_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE


ie_faturamento_direto_w	varchar(1);


BEGIN


select	coalesce(max(ie_faturamento_direto),'N')
into STRICT	ie_faturamento_direto_w
from	material_autorizado
where	nr_prescricao 		= nr_prescricao_p
and	nr_seq_prescricao 	= nr_seq_material_p;

return	ie_faturamento_direto_w;

end;
$body$
LANGUAGE PLPGSQL
