CREATE OR REPLACE FUNCTION obter_se_fecha_pepo ( nr_cirurgia_p bigint) RETURNS varchar AS $body$
DECLARE


qt_saldo_w	integer;
ds_retorno_w	varchar(255) := 'S';

expressao1_w	varchar(255) := obter_desc_expressao_idioma(774330, null, wheb_usuario_pck.get_nr_seq_idioma);--Existem itens de controle de cavidade com saldo positivo. Favor verificar!
BEGIN

if (nr_cirurgia_p > 0) then
	select	sum(CASE WHEN ie_acao=1 THEN qt_item  ELSE 0 END  - CASE WHEN ie_acao=2 THEN qt_item WHEN ie_acao=3 THEN qt_item WHEN ie_acao=4 THEN qt_item  ELSE 0 END )
	into STRICT	qt_saldo_w
	from	pepo_item_controle b,
		cirurgia_item_controle a
	where	a.nr_seq_item_controle 	= b.nr_sequencia
	and	nr_cirurgia		= nr_cirurgia_p
	and	coalesce(a.ie_situacao,'A') 	= 'A'
	and	coalesce(b.ie_situacao,'A') 	= 'A'
	and	b.ie_controla_saldo = 'S';

	if (qt_saldo_w > 0) then
		ds_retorno_w	:= expressao1_w;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
