CREATE OR REPLACE FUNCTION obter_se_fecha_turno_temp (nr_seq_setor_temp_p bigint) RETURNS varchar AS $body$
DECLARE


cont_w		bigint;


BEGIN

select	coalesce(count(*),0)
into STRICT	cont_w
from	setor_temperatura
where	nr_sequencia	= nr_seq_setor_temp_p
and (coalesce(dt_hora_fim::text, '') = ''
or	 coalesce(qt_temp_inicial::text, '') = ''
or	 coalesce(qt_temp_min::text, '') = ''
or	 coalesce(qt_temp_max::text, '') = ''
or	 coalesce(qt_umidade_final::text, '') = ''
or	 coalesce(nr_seq_turno::text, '') = '');

if (coalesce(cont_w,0) = 0) then
	select	coalesce(count(*),0)
	into STRICT	cont_w
	from	setor_temperatura_item
	where	nr_seq_setor_temp = nr_seq_setor_temp_p
	and (coalesce(qt_temp_min::text, '') = ''
	or	 coalesce(qt_temp_max::text, '') = ''
	or	 coalesce(qt_umidade_final::text, '') = '');
end if;

if (coalesce(cont_w,0) = 0) then
	return 'S';
else
	return 'N';
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
