CREATE OR REPLACE FUNCTION obter_se_feriado (cd_estabelecimento_p bigint, dt_parametro_p timestamp) RETURNS bigint AS $body$
DECLARE


ie_feriado_w	integer;
dt_parametro_w	timestamp;


BEGIN

dt_parametro_w	:= ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(dt_parametro_p);

select	count(*)
into STRICT	ie_feriado_w
from	feriado
where	cd_estabelecimento	= cd_estabelecimento_p
and	dt_feriado		= dt_parametro_w;

RETURN ie_feriado_w;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
