CREATE OR REPLACE FUNCTION obter_se_ferias_hn (cd_pessoa_fisica_p bigint) RETURNS varchar AS $body$
DECLARE


vl_retorno_w	varchar(200);


BEGIN
	SELECT      DISTINCT   TO_CHAR(j.DT_ADMISSAO_HOSP + 365, 'dd/mm/yyyy')
	INTO STRICT		vl_retorno_w
	FROM   	 	pessoa_fisica j
	WHERE		(j.dt_admissao_hosp IS NOT NULL AND j.dt_admissao_hosp::text <> '')
	AND		j.cd_pessoa_fisica = cd_pessoa_fisica_p
	and		j.DT_ADMISSAO_HOSP > (clock_timestamp() - interval '10000 days');

RETURN vl_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
