CREATE OR REPLACE FUNCTION obter_se_ficha_lib_encerrada ( nr_ficha_ocorrencia_p bigint) RETURNS varchar AS $body$
DECLARE

ds_retorno_w	varchar(1);

BEGIN

if (coalesce(nr_ficha_ocorrencia_p,0) > 0) then

	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ds_retorno_w
	from	cih_ficha_ocorrencia
	where	nr_ficha_ocorrencia = nr_ficha_ocorrencia_p
	and (coalesce(dt_liberacao::text, '') = '' and coalesce(dt_encerramento::text, '') = '');
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
