CREATE OR REPLACE FUNCTION obter_se_fila_chama_monitor ( nr_seq_fila_p bigint, ds_maquina_monitor_p text, cd_estabelecimento_p bigint default 1) RETURNS varchar AS $body$
DECLARE


ie_fila_liberada_w	varchar(1) := 'N';
NR_SEQ_LOCAL_SENHA_w	bigint;

C01 CURSOR FOR
	SELECT	a.nr_sequencia
	from	maquina_local_senha a,
		computador b
	where	b.nm_computador = ds_maquina_monitor_p
	and	a.nr_seq_comp_monitor = b.nr_sequencia
	and	b.cd_estabelecimento = coalesce(cd_estabelecimento_p, 1)
	
union

	SELECT	a.nr_seq_local
	from	MAQUINA_MONITOR_ADICIONAL a,
		computador b
	where	b.nm_computador = ds_maquina_monitor_p
	and	a.nr_seq_comp_monitor = b.nr_sequencia
	and	b.cd_estabelecimento = coalesce(cd_estabelecimento_p, 1);


BEGIN

open C01;
loop
fetch C01 into
	NR_SEQ_LOCAL_SENHA_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
		select	'S'
		into STRICT	ie_fila_liberada_w
		from	REGRA_LIBERACAO_FILA
		where	NR_SEQ_FILA_ESPERA = nr_seq_fila_p
		and		NR_SEQ_LOCAL_SENHA = NR_SEQ_LOCAL_SENHA_w  LIMIT 1;

		exit;

	exception
	when others then
		ie_fila_liberada_w := 'N';
	end;
end loop;
close C01;

return	ie_fila_liberada_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
