CREATE OR REPLACE FUNCTION obter_se_fim_consulta_oftalmo ( nr_seq_consulta_p bigint) RETURNS varchar AS $body$
DECLARE


ie_fim_consulta_w	varchar(5) := 'N';


BEGIN

if (nr_seq_consulta_p IS NOT NULL AND nr_seq_consulta_p::text <> '') then
	begin
	SELECT  coalesce(MAX('S'),'N')
	into STRICT 	ie_fim_consulta_w
	FROM    oft_consulta
	WHERE   nr_sequencia = nr_seq_consulta_p
	AND     (DT_FIM_CONSULTA IS NOT NULL AND DT_FIM_CONSULTA::text <> '')
	and		coalesce(dt_cancelamento::text, '') = '';
	end;
end if;

return ie_fim_consulta_w;

end;
