CREATE OR REPLACE FUNCTION obter_se_finaliza_atend ( nr_atendimento_p bigint, cd_motivo_alta_p bigint) RETURNS varchar AS $body$
DECLARE

 
			 
qt_registros_w		bigint;		
cd_convenio_w		integer;
ie_tipo_atendimento_w	smallint;
cd_setor_Atendimento_w	integer;
cd_estabelecimento_w	integer;	
ie_tipo_convenio_w		varchar(2);
cd_medico_resp_w		varchar(10);
nr_seq_classificacao_w	bigint;
cd_categoria_w		categoria_convenio.cd_categoria%type;
			

BEGIN 
begin 
select	cd_setor_atendimento, 
	ie_tipo_atendimento, 
	cd_convenio, 
	cd_estabelecimento, 
	substr(obter_tipo_convenio(cd_convenio),1,2), 
	cd_medico_resp, 
	coalesce(nr_seq_classificacao,0), 
	cd_categoria 
into STRICT	cd_setor_Atendimento_w, 
	ie_tipo_atendimento_w, 
	cd_convenio_w, 
	cd_estabelecimento_w, 
	ie_tipo_convenio_w, 
	cd_medico_resp_w, 
	nr_seq_classificacao_w, 
	cd_categoria_w 
from	atendimento_paciente_v 
where 	nr_atendimento	= nr_atendimento_p;
exception 
	when others then 
	null;
	end;
	 
select	 count(*) 
into STRICT	qt_registros_w 
from	regra_fechar_conta_alta 
where	cd_estabelecimento					= cd_estabelecimento_w 
and	coalesce(cd_convenio,cd_convenio_w)	 		= cd_convenio_w 
and	coalesce(cd_categoria,cd_categoria_w)	 		= cd_categoria_w 
and	coalesce(ie_tipo_atendimento,ie_tipo_atendimento_w)	 	= ie_tipo_atendimento_w 
and	coalesce(cd_setor_atendimento,cd_setor_Atendimento_w)	= cd_setor_Atendimento_w 
and	((coalesce(ie_tipo_convenio, coalesce(ie_tipo_convenio_w, 0)) = coalesce(ie_tipo_convenio_w, 0)) or (coalesce(ie_tipo_convenio_w, 0) = 0)) 
and	coalesce(cd_motivo_alta, coalesce(cd_motivo_alta_p, 0)) = coalesce(cd_motivo_alta_p, 0) 
and	coalesce(Obter_Se_Regra_Alta_Proc(nr_atendimento_p, nr_sequencia),'S') = 'S' 
and	coalesce(cd_medico_resp, coalesce(cd_medico_resp_w,0)) = coalesce(cd_medico_resp_w,0) 
and	coalesce(cd_perfil, coalesce(obter_perfil_ativo,0)) = coalesce(obter_perfil_ativo,0) 
and	coalesce(nr_seq_classificacao, coalesce(nr_seq_classificacao_w,0)) = coalesce(nr_seq_classificacao_w,0);
 
if (qt_registros_w		= 0) then 
	return	'N';
else 
	return 'S';
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
