CREATE OR REPLACE FUNCTION obter_se_final_checkup ( nr_atendimento_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


qt_existe_w	bigint;


BEGIN

if (ie_opcao_p = 'N') then
	begin
	select	count(*)
	into STRICT	qt_existe_w
	from	checkup
	where	nr_atendimento	= nr_atendimento_p
	and	(dt_fim_real IS NOT NULL AND dt_fim_real::text <> '');
	end;
else
	begin
	select	count(*)
	into STRICT	qt_existe_w
	from	checkup
	where	nr_atendimento	= nr_atendimento_p
	and	(dt_fim_interno IS NOT NULL AND dt_fim_interno::text <> '');
	end;
end if;

if (qt_existe_w > 0) then
	return 'S';
else
	return 'N';
end if;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
