CREATE OR REPLACE FUNCTION obter_se_fleury_dados_prescr ( nr_prescricao_p bigint, nr_seq_prescr_p bigint, ie_tipo_p text) RETURNS varchar AS $body$
DECLARE


ie_retorno_w	varchar(5) := 'N';
qt_itens_w	bigint;


BEGIN

if (ie_tipo_p = 'MAT') then

	select	count(*)
	into STRICT	qt_itens_w
	from 	fleury_prescr_proc_mat
	where 	nr_prescricao = nr_prescricao_p
	and 	nr_seq_prescr = nr_seq_prescr_p;

	if (qt_itens_w > 0) then
		ie_retorno_w := 'S';
	end if;

elsif (ie_tipo_p = 'PER') then

	select	coalesce(max(ie_possui_pergunta_tecnica),'N')
	into STRICT	ie_retorno_w
	from	fleury_prescr_proced_valid
	where 	nr_prescricao = nr_prescricao_p
	and 	nr_seq_prescr = nr_seq_prescr_p;

end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
