CREATE OR REPLACE FUNCTION obter_se_fluxo_esp (cd_estabelecimento_p bigint, cd_conta_financ_p bigint, dt_referencia_p timestamp) RETURNS varchar AS $body$
DECLARE


cont_w			bigint;
ie_retorno_w		varchar(100);
IE_FLUXO_ESPECIAL_w	varchar(100);


BEGIN

select	IE_FLUXO_ESPECIAL
into STRICT	IE_FLUXO_ESPECIAL_w
from	parametro_fluxo_caixa
where	cd_estabelecimento	= cd_estabelecimento_p;

select	count(*)
into STRICT	cont_w
from	fluxo_caixa
where	cd_estabelecimento	= cd_estabelecimento_p
and	dt_referencia		= trunc(dt_referencia_p, 'dd')
and	cd_conta_financ	= cd_conta_financ_p
and	ie_periodo		= 'D'
and	ie_classif_fluxo	= 'R'
and	ie_integracao <> 'RE';

ie_retorno_w		:= 'S';
if (IE_FLUXO_ESPECIAL_w = 'N') and (cont_w > 0) then
	ie_retorno_w		:= 'N';
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
