CREATE OR REPLACE FUNCTION obter_se_fluxo_lote_esp ( cd_estabelecimento_p bigint, cd_conta_financ_p bigint, dt_referencia_p timestamp) RETURNS varchar AS $body$
DECLARE


cont_w			bigint;
ie_retorno_w		varchar(100);
ie_fluxo_especial_w	parametro_fluxo_caixa.ie_fluxo_especial%type;
dt_referencia_w		fluxo_caixa_data.dt_referencia%type;


BEGIN

dt_referencia_w	:= trunc(dt_referencia_p,'dd');

select	ie_fluxo_especial
into STRICT	ie_fluxo_especial_w
from	parametro_fluxo_caixa
where	cd_estabelecimento	= cd_estabelecimento_p;

select	count(*)
into STRICT	cont_w
from	fluxo_caixa_lote a,
	fluxo_caixa_data b
where	b.cd_estabelecimento	= cd_estabelecimento_p
and	b.dt_referencia		= dt_referencia_w
and	b.cd_conta_financ	= cd_conta_financ_p
and	a.ie_classif_fluxo	= 'R';

ie_retorno_w	:= 'S';
if (ie_fluxo_especial_w = 'N') and (cont_w > 0) then
	ie_retorno_w	:= 'N';
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
