CREATE OR REPLACE FUNCTION obter_se_fora_periodo_atend (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

 
ie_fora_periodo_atend_w		varchar(1):= 'N';
nr_interno_conta_w		conta_paciente.nr_interno_conta%type;
cd_convenio_parametro_w		conta_paciente.cd_convenio_parametro%type;
cd_categoria_parametro_w	conta_paciente.cd_categoria_parametro%type;
ie_tipo_w			varchar(1);
nr_sequencia_w			bigint;
dt_item_w			timestamp;
qt_existe_dentro_periodo_w	bigint;

C01 CURSOR FOR 
	SELECT	nr_interno_conta, 
		cd_convenio_parametro, 
		cd_categoria_parametro 
	from	conta_paciente 
	where	nr_atendimento = nr_atendimento_p 
	and 	ie_status_acerto = 1 
	order by nr_interno_conta;

 
C02 CURSOR FOR 
	SELECT	'P', 
		nr_sequencia, 
		dt_procedimento 
	from	procedimento_paciente 
	where	nr_interno_conta = nr_interno_conta_w 
	and 	coalesce(cd_motivo_exc_conta::text, '') = '' 
	
union all
 
	SELECT	'M', 
		nr_sequencia, 
		dt_atendimento 
	from	material_atend_paciente 
	where	nr_interno_conta = nr_interno_conta_w 
	and 	coalesce(cd_motivo_exc_conta::text, '') = '' 
	order by 1,2;
			

BEGIN 
 
-- Lendo as contas com status provisório 
open C01;
loop 
fetch C01 into	 
	nr_interno_conta_w, 
	cd_convenio_parametro_w, 
	cd_categoria_parametro_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	 
	-- Lendo as informações de data dos itens dessas contas 
	open C02;
	loop 
	fetch C02 into	 
		ie_tipo_w, 
		nr_sequencia_w, 
		dt_item_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin 
		 
		if (ie_fora_periodo_atend_w = 'N') then 
		 
			select 	count(*) 
			into STRICT	qt_existe_dentro_periodo_w 
			from 	atend_categoria_convenio 
			where 	nr_atendimento = nr_atendimento_p 
			and 	cd_convenio = cd_convenio_parametro_w 
			and 	cd_categoria = cd_categoria_parametro_w 
			and 	dt_item_w between dt_inicio_vigencia and coalesce(dt_final_vigencia, dt_item_w);
		 
			-- Se não encontrou período do atendimento para o item, então está fora do período. 
			if (qt_existe_dentro_periodo_w = 0) then 
				ie_fora_periodo_atend_w:= 'S';
			end if;
			 
		end if;
		 
		end;
	end loop;
	close C02;		
	 
	end;
end loop;
close C01;
 
return	ie_fora_periodo_atend_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_se_fora_periodo_atend (nr_atendimento_p bigint) FROM PUBLIC;

