CREATE OR REPLACE FUNCTION obter_se_forma_agend_lib (nr_seq_forma_agendamento_p bigint, cd_perfil_p bigint, ie_agenda_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1);
qt_regra_w	bigint;


BEGIN

select	count(*)
into STRICT	qt_regra_w
from	agenda_regra_forma_agend
where	nr_seq_forma_agendamento = nr_seq_forma_agendamento_p;

if (qt_regra_w > 0) then
	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ds_retorno_w
	from	agenda_regra_forma_agend
	where	nr_seq_forma_agendamento = nr_seq_forma_agendamento_p
	and	((coalesce(ie_agenda,'C') = ie_agenda_p) or (coalesce(ie_agenda,'C') = 'T'))
	and	cd_perfil = cd_perfil_p;
else
	ds_retorno_w := 'S';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
